package com.example.mylibrary;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;

import java.util.ArrayList;

public class AllBooksActivity extends AppCompatActivity {
private RecyclerView booksRecView;
private BookRecViewAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_all_books);

        adapter = new BookRecViewAdapter(this);
        booksRecView = findViewById(R.id.booksRecView);

        ArrayList<Book> books = new ArrayList<>();

        books.add(new Book(1," T.O.C", "Peter Linz","data:image/jpeg;base64,"));
        books.add(new Book(2," Computer Network", "Forouzn","data:image/jpeg;base64,"));
        books.add( new Book(3," Operating System", "Galvin","data:image/jpeg;base64,"));
        books.add(new Book(4," Compiler Design", "Ullman","data:image/jpeg;base64,"));
        books.add(new Book(5,"Digital Logic", "Morris Mano","data:image/jpeg;base64,"));
        books.add(new Book(6," COA", "Morris Mano","data:image/jpeg;base64,"));
        books.add(new Book(7," Algorithm", "Cormen","data:image/jpeg;base64,"));
        books.add(new Book(8," DataBase", "Hennessy","data:image/jpeg;base64,"));




        booksRecView.setAdapter(adapter);
        booksRecView.setLayoutManager(new LinearLayoutManager(this));


        adapter.setBooks(books);


    }
}
